children.text:
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: Georgia, serif; line-height: 1.8; color: #333; background: white; }

header { background: #2c3e50; color: white; padding: 3rem 1rem; text-align: center; }
header h1 { font-size: 2.5rem; margin-bottom: 1rem; }
.meta { font-size: 0.95rem; opacity: 0.9; }

.container { max-width: 1200px; margin: 0 auto; display: grid; grid-template-columns: 1fr 300px; gap: 2rem; padding: 2rem 1rem; }

/* Main Article */
article { line-height: 1.9; }
article h2 { font-size: 1.8rem; margin: 2rem 0 1rem 0; color: #2c3e50; }
article h3 { font-size: 1.3rem; margin: 1.5rem 0 0.75rem 0; color: #34495e; }
article p { margin-bottom: 1.5rem; color: #555; }

.highlight { background: #fffbea; padding: 1rem; border-left: 4px solid #f39c12; margin: 1.5rem 0; }
.highlight p { margin: 0; }

code { background: #f4f4f4; padding: 2px 6px; border-radius: 3px; font-family: 'Monaco', monospace; font-size: 0.9rem; }

blockquote { border-left: 4px solid #3498db; padding-left: 1.5rem; margin: 2rem 0; color: #7f8c8d; font-style: italic; }

ul, ol { margin-bottom: 1.5rem; padding-left: 2rem; }
li { margin-bottom: 0.5rem; }

/* Sidebar */
aside { background: #f9f9f9; padding: 1.5rem; border-radius: 8px; height: fit-content; }
aside h3 { color: #2c3e50; margin-bottom: 1rem; font-size: 1.1rem; }
aside nav ul { list-style: none; padding: 0; }
aside nav li { margin-bottom: 0.75rem; }
aside nav a { color: #3498db; text-decoration: none; }
aside nav a:hover { text-decoration: underline; }

.toc-section { margin-bottom: 2rem; }
.toc-section strong { display: block; margin-bottom: 0.5rem; color: #2c3e50; font-size: 0.95rem; }

footer { background: #ecf0f1; padding: 2rem 1rem; text-align: center; color: #666; }

@media (max-width: 768px) {
  .container { grid-template-columns: 1fr; }
}
