/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: 'Arial', sans-serif; font-size: 16px; }

/* Test-specific CSS */
.container {
    width: 500px;
    height: 200px;
    display: flex;
    gap: 15px;
    border: 2px solid #333;
    padding: 10px;
}
.item {
    flex: 1;
    padding: 15px;
    box-sizing: border-box;
    text-align: left;
    line-height: 1.4;
}
.item:nth-child(1) { 
    background-color: #ffeeee; 
    text-align: center;
}
.item:nth-child(2) { 
    background-color: #eeffee; 
    text-align: left;
}
.item:nth-child(3) { 
    background-color: #eeeeff; 
    text-align: right;
}
.highlight {
    font-weight: bold;
    color: #d63384;
}
.small-text {
    font-size: 12px;
    color: #666;
}
