children.text:
/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: 'Arial', sans-serif; font-size: 16px; }

/* Test-specific CSS */
.container {
    width: 400px;
    height: 500px;
    display: flex;
    flex-direction: column;
    gap: 15px;
    border: 2px solid #333;
    padding: 15px;
}
.item {
    flex: 1;
    padding: 15px;
    box-sizing: border-box;
    background-color: #f8f9fa;
    border: 1px solid #dee2e6;
}
h4 {
    margin-bottom: 10px;
    font-size: 14px;
    color: #495057;
    text-align: center;
}
.text-content {
    margin: 8px 0;
    font-size: 13px;
    line-height: 1.4;
}
.nested-section {
    margin: 10px 0;
    padding: 10px;
    background-color: #e9ecef;
    border-left: 3px solid #6c757d;
}
ul {
    margin: 8px 0;
    padding-left: 18px;
}
li {
    margin: 3px 0;
    font-size: 12px;
}
.highlight {
    background-color: #fff3cd;
    padding: 2px 4px;
    font-weight: bold;
}
.center-text {
    text-align: center;
    font-style: italic;
    color: #6c757d;
    margin: 8px 0;
}
