/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: 'Arial', sans-serif; font-size: 16px; }

/* Test-specific CSS */
.container {
    width: 500px;
    height: 300px;
    display: flex;
    gap: 15px;
    border: 2px solid #333;
    padding: 15px;
}
.item {
    flex: 1;
    padding: 12px;
    box-sizing: border-box;
    background-color: #f8f9fa;
    border: 1px solid #dee2e6;
    font-size: 13px;
    line-height: 1.5;
}
.narrow {
    width: 120px;
    flex: none;
}
.wide {
    flex: 2;
}
.long-text {
    word-wrap: break-word;
    hyphens: auto;
}
.no-wrap {
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.pre-wrap {
    white-space: pre-wrap;
}
h5 {
    margin-bottom: 8px;
    font-size: 12px;
    font-weight: bold;
}
