/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body {
    font-family: 'Arial', sans-serif;
    font-size: 16px;
    line-height: 1.5;
    padding: 20px;
    max-width: 800px;
}

/* Image container styles */
.image-container {
    margin: 20px 0;
}

/* Image styles */
img {
    max-width: 100%;
    height: auto;
    display: block;
    margin: 12px 0;
    border: 1px solid #ddd;
}

h1 {
    font-size: 24px;
    margin-bottom: 20px;
    font-weight: bold;
    color: #333;
}

h2 {
    font-size: 20px;
    margin-top: 24px;
    margin-bottom: 12px;
    font-weight: bold;
    color: #555;
}

p {
    margin-bottom: 12px;
    color: #666;
}
