/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: 'Arial', sans-serif; font-size: 16px; }

/* Test container */
.test-container {
    width: 600px;
    height: 700px;
    border: 2px solid #333;
    padding: 20px;
    background-color: #f9f9f9;
}

/* Common box styles */
.example-section {
    margin: 20px 0;
    border: 1px dashed #999;
    padding: 15px;
    background-color: white;
    position: relative;
}

.section-title {
    font-weight: bold;
    margin-bottom: 10px;
    color: #333;
}

.box {
    width: 200px;
    height: 60px;
    border: 1px solid #666;
    text-align: center;
    line-height: 60px;
    font-size: 14px;
}

/* Horizontal centering */
.margin-auto {
    margin: 10px auto;
    background-color: #ffcccc;
}

/* Absolute positioning centering */
.absolute-container {
    position: relative;
    height: 100px;
    background-color: #f0f0f0;
}

.absolute-center {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    background-color: #ccffcc;
}

.absolute-horizontal {
    position: absolute;
    left: 50%;
    top: 20px;
    transform: translateX(-50%);
    background-color: #ccccff;
}

.absolute-vertical {
    position: absolute;
    top: 50%;
    left: 20px;
    transform: translateY(-50%);
    background-color: #ffffcc;
}

/* Table-cell centering */
.table-container {
    display: table-cell;
    width: 300px;
    height: 100px;
    vertical-align: middle;
    text-align: center;
    border: 1px solid #ccc;
    background-color: #f0f0f0;
}

.table-centered {
    display: inline-block;
    background-color: #ffccff;
}
