Test: Nested Box Models

        /* Reset for consistency */
        * { margin: 0; padding: 0; box-sizing: border-box; }
        body { font-family: 'Arial', sans-serif; font-size: 16px; }

        /* Test container */
        .test-container {
            width: 800px;
            height: 800px;
            border: 2px solid #333;
            padding: 20px;
            background-color: #f9f9f9;
        }

        /* Test-specific CSS */
        .nested-test {
            margin: 20px 0;
            padding: 15px;
            border: 1px dashed #999;
            background-color: white;
        }

        .test-title {
            font-weight: bold;
            margin-bottom: 10px;
            color: #333;
        }

        /* Outer container */
        .outer-box {
            width: 400px;
            height: 200px;
            margin: 20px;
            padding: 20px;
            border: 3px solid #666;
            background-color: #ffcccc;
            position: relative;
        }

        /* Middle container */
        .middle-box {
            width: 80%;
            height: 70%;
            margin: 10px;
            padding: 15px;
            border: 2px solid #333;
            background-color: #ccffcc;
            position: relative;
        }

        /* Inner container */
        .inner-box {
            width: 60%;
            height: 50%;
            margin: 5px;
            padding: 10px;
            border: 1px solid #000;
            background-color: #ccccff;
            text-align: center;
            font-size: 12px;
            line-height: 1.2;
        }

        /* Margin collapse demonstration */
        .margin-collapse-parent {
            background-color: #ffffcc;
            border: 3px solid #666;
            /* No padding to allow margin collapse */
            width: 300px;
            margin: 20px;
        }

        .margin-collapse-child {
            margin: 30px;
            padding: 15px;
            background-color: #ffccff;
            border: 1px solid #333;
        }

        /* Margin collapse prevented */
        .margin-no-collapse-parent {
            background-color: #ccffff;
            border: 3px solid #666;
            padding: 1px; /* Prevents margin collapse */
            width: 300px;
            margin: 20px;
        }

        .margin-no-collapse-child {
            margin: 30px;
            padding: 15px;
            background-color: #ffccaa;
            border: 1px solid #333;
        }

        /* Different box-sizing inheritance */
        .content-box-parent {
            box-sizing: content-box;
            width: 200px;
            height: 100px;
            padding: 20px;
            border: 5px solid #666;
            background-color: #ffaacc;
            margin: 10px;
        }

        .border-box-child {
            box-sizing: border-box;
            width: 100%;
            height: 100%;
            padding: 10px;
            border: 2px solid #333;
            background-color: #aaffcc;
        }

        /* Labels for measurements */
        .label {
            position: absolute;
            background-color: rgba(255, 255, 255, 0.9);
            padding: 2px 5px;
            font-size: 10px;
            border: 1px solid #999;
            z-index: 10;
        }

        .label-outer { top: -15px; left: 0; }
        .label-middle { top: 25px; right: -60px; }
        .label-inner { bottom: -15px; left: 0; }





