/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: 'Arial', sans-serif; font-size: 16px; }

/* Test-specific CSS */
.container {
    width: 400px;
    height: 300px;
    display: grid;
    grid-template-columns: 1fr 2fr 3fr;
    grid-template-rows: 100px 1fr 80px;
    gap: 10px;
    border: 2px solid #333;
    padding: 10px;
}
.item {
    padding: 10px;
    box-sizing: border-box;
    text-align: center;
    font-size: 14px;
}
.item:nth-child(1) { background-color: #ffcccc; }
.item:nth-child(2) { background-color: #ccffcc; }
.item:nth-child(3) { background-color: #ccccff; }
.item:nth-child(4) { background-color: #ffffcc; }
.item:nth-child(5) { background-color: #ffccff; }
.item:nth-child(6) { background-color: #ccffff; }
.item:nth-child(7) { background-color: #ffeedd; }
.item:nth-child(8) { background-color: #ddffee; }
.item:nth-child(9) { background-color: #eeddff; }
