children.text:
body {
    font-family: Georgia, "Times New Roman", Times, serif;
    font-size: 16px;
    line-height: 1.6;
    max-width: 800px;
    margin: 40px auto;
    padding: 20px;
    color: #333;
}

h1 {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-weight: bold;
    font-size: 28px;
    margin-bottom: 30px;
}

h2 {
    font-family: Arial, sans-serif;
    font-weight: bold;
    font-size: 20px;
    margin-top: 30px;
    border-bottom: 1px solid #ccc;
    padding-bottom: 5px;
}

/* Issue 1: TTC Bold Font - Tests Helvetica Bold from TTC file */
.ttc-bold-test {
    font-family: Helvetica, Arial, sans-serif;
    font-weight: 700;
    font-size: 18px;
    color: #000;
}

/* Issue 2: Multi-word font family names */
.multiword-font-test {
    font-family: "Linux Libertine", "Times New Roman", Georgia, serif;
    font-size: 18px;
}

/* Issue 3: Font family fallback - should use first available */
.fallback-test {
    font-family: "NonExistent Font", "Another Fake Font", Georgia, Arial, sans-serif;
    font-size: 18px;
}

/* Link color test (bonus - rgb() modern syntax) */
a {
    color: rgb(8 145 178);
    text-decoration: underline;
}

a:hover {
    color: rgb(6 182 212);
}

.test-box {
    background: #f5f5f5;
    border: 1px solid #ddd;
    padding: 15px;
    margin: 10px 0;
    border-radius: 4px;
}

.pass { color: green; }
.label { color: #666; font-size: 14px; }
