children.text:
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: Arial, sans-serif; font-size: 14px; padding: 20px; }

.label { font-size: 12px; color: #666; margin: 10px 0 5px; }

/* Test 1: Simple 2x2 grid */
.grid1 {
  display: grid;
  grid-template-columns: 200px 200px;
  grid-template-rows: 100px 100px;
  gap: 10px;
  background: #f0f0f0;
  padding: 10px;
  margin-bottom: 30px;
}

/* Test 2: fr units */
.grid2 {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 10px;
  background: #f0f0f0;
  padding: 10px;
  margin-bottom: 30px;
}

/* Test 3: Mixed units */
.grid3 {
  display: grid;
  grid-template-columns: 100px 1fr 100px;
  gap: 10px;
  background: #f0f0f0;
  padding: 10px;
  margin-bottom: 30px;
}

.item {
  background: #4a90d9;
  color: white;
  padding: 20px;
  text-align: center;
}
