/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { 
    font-family: 'Arial', sans-serif; 
    font-size: 16px; 
    line-height: 1.5;
    padding: 20px;
    max-width: 700px;
}

/* Nested list styles */
ul, ol {
    margin-bottom: 16px;
    padding-left: 24px;
}

ul ul, ol ul, ul ol, ol ol {
    margin-bottom: 0;
    margin-top: 8px;
}

li {
    margin-bottom: 8px;
    line-height: 1.5;
}

li:last-child {
    margin-bottom: 0;
}

h3 {
    font-size: 20px;
    margin-bottom: 12px;
    font-weight: bold;
}

p {
    margin-bottom: 16px;
}
