Test: Document Structure Combination

        /* Reset for consistency */
        * { margin: 0; padding: 0; box-sizing: border-box; }
        body { 
            font-family: 'Arial', sans-serif; 
            font-size: 16px; 
            line-height: 1.6;
            padding: 20px;
            max-width: 800px;
        }

        /* Typography */
        h1 {
            font-size: 32px;
            margin-bottom: 16px;
            font-weight: bold;
        }

        h2 {
            font-size: 24px;
            margin-bottom: 12px;
            margin-top: 32px;
            font-weight: bold;
        }

        h3 {
            font-size: 20px;
            margin-bottom: 12px;
            margin-top: 24px;
            font-weight: bold;
        }

        p {
            margin-bottom: 16px;
        }

        /* Lists */
        ul, ol {
            margin-bottom: 16px;
            padding-left: 24px;
        }

        li {
            margin-bottom: 8px;
        }

        /* Code */
        code {
            font-family: 'Courier New', monospace;
            font-size: 14px;
            background-color: #f3f4f6;
            padding: 2px 4px;
            border-radius: 3px;
        }

        pre {
            background-color: #f6f8fa;
            border: 1px solid #d1d9e0;
            border-radius: 6px;
            padding: 16px;
            margin: 16px 0;
            overflow-x: auto;
            font-family: 'Courier New', monospace;
            font-size: 14px;
            line-height: 1.4;
        }

        /* Quotes */
        blockquote {
            margin: 16px 0;
            padding: 16px 20px;
            border-left: 4px solid #ddd;
            background-color: #f9f9f9;
            font-style: italic;
            color: #666;
        }

        /* Separators */
        hr {
            border: none;
            height: 1px;
            background-color: #d1d9e0;
            margin: 24px 0;
        }

        /* Emphasis */
        .emphasis { font-style: italic; }
        .strong { font-weight: bold; }



