/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: 'Arial', sans-serif; font-size: 16px; }

/* Test-specific CSS */
.container {
    width: 400px;
    height: 300px;
    display: table;
    table-layout: auto;
    border: 2px solid #333;
    padding: 10px;
    border-spacing: 10px;
}
.row {
    display: table-row;
}
.cell {
    display: table-cell;
    padding: 10px;
    box-sizing: border-box;
    text-align: center;
    font-size: 14px;
}
.cell:nth-child(1) { background-color: #ffcccc; }
.cell:nth-child(2) { background-color: #ccffcc; }
.cell:nth-child(3) { background-color: #ccccff; }
