/* Reset for consistency */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { 
    font-family: 'Arial', sans-serif; 
    font-size: 16px; 
    line-height: 1.6;
    padding: 20px;
    max-width: 700px;
}

/* Typography */
h3 {
    font-size: 20px;
    margin-bottom: 12px;
    font-weight: bold;
}

p {
    margin-bottom: 16px;
}

/* Links */
a {
    color: #0066cc;
    text-decoration: underline;
}

a:hover {
    color: #004499;
}

a:visited {
    color: #6611cc;
}

/* Text formatting */
.strong {
    font-weight: bold;
}

.emphasis {
    font-style: italic;
}

.code {
    font-family: 'Courier New', monospace;
    font-size: 14px;
    background-color: #f3f4f6;
    padding: 2px 4px;
    border-radius: 3px;
}

.strikethrough {
    text-decoration: line-through;
}

/* Special formatting combinations */
.highlight {
    background-color: #ffeb3b;
    padding: 1px 2px;
}
